mapper.update:
UPDATE MEMBER_S SET 
MEMBER_PW = #{memberPw} 
WHERE MEMBER_NO = ${memberNo} 
AND SECESSION_FL = 'N'

mapper.select:
SELECT count(*) FROM MEMBER_S 
WHERE MEMBER_NICK = #{memberNickname}
AND SECESSION_FL = 'N'

mapper.update:
UPDATE MEMBER_S SET  
MEMBER_NICK = #{memberNickname}, 
MEMBER_TEL = #{memberTel}, 
MEMBER_ADDR = #{memberAddress} 
WHERE MEMBER_NO = ${memberNo} 
AND SECESSION_FL = 'N'

UPDATE MEMBER_S SET  
MEMBER_TEL = #{newTel}, 
MEMBER_ADDR = #{memberAddress} 
WHERE MEMBER_NO = ${memberNo} 
AND SECESSION_FL = 'N'

UPDATE MEMBER_S SET  
MEMBER_NICK = #{newNickname}, 
MEMBER_TEL = #{newTel}, 
MEMBER_ADDR = #{memberAddress} 
WHERE MEMBER_NO = ${memberNo} 
AND SECESSION_FL = 'N'
